svg.script:

function copyPrompt(text) {
  if (navigator.clipboard && window.isSecureContext) {
    navigator.clipboard.writeText(text).then(function() {
      showCopyMessage();
    }).catch(function(err) {
      console.error('コピーに失敗しました: ', err);
    });
  } else {
    // フォールバック: テキストエリアを使用
    const textArea = document.createElement('textarea');
    textArea.value = text;
    document.body.appendChild(textArea);
    textArea.select();
    try {
      document.execCommand('copy');
      showCopyMessage();
    } catch (err) {
      console.error('コピーに失敗しました: ', err);
    }
    document.body.removeChild(textArea);
  }
}

function showCopyMessage() {
  // 既存のメッセージを削除
  const existingMessage = document.getElementById('copyMessage');
  if (existingMessage) {
    existingMessage.remove();
  }
  
  // コピー完了メッセージを表示
  const message = document.createElementNS('http://www.w3.org/2000/svg', 'g');
  message.setAttribute('id', 'copyMessage');
  
  const rect = document.createElementNS('http://www.w3.org/2000/svg', 'rect');
  rect.setAttribute('x', '750');
  rect.setAttribute('y', '300');
  rect.setAttribute('width', '200');
  rect.setAttribute('height', '50');
  rect.setAttribute('rx', '10');
  rect.setAttribute('fill', '#32cd32');
  rect.setAttribute('opacity', '0.9');
  
  const text = document.createElementNS('http://www.w3.org/2000/svg', 'text');
  text.setAttribute('x', '850');
  text.setAttribute('y', '330');
  text.setAttribute('text-anchor', 'middle');
  text.setAttribute('fill', '#ffffff');
  text.setAttribute('font-family', 'Arial, sans-serif');
  text.setAttribute('font-size', '16');
  text.setAttribute('font-weight', 'bold');
  text.textContent = '✓ コピーしました！';
  
  message.appendChild(rect);
  message.appendChild(text);
  document.querySelector('svg').appendChild(message);
  
  // 2秒後にメッセージを削除
  setTimeout(function() {
    const msg = document.getElementById('copyMessage');
    if (msg) {
      msg.remove();
    }
  }, 2000);
}
